class Calculator :    def __init__(self,x,y):        self.x = x        self.y = y    def add(self):        return self.x + self.y    def sub(self):        return self.x - self.y    def mul(self):       return self.x * self.y    def div(self):        return round(self.x / self.y,2)    def pow(self):        return self.x ** self.yclass Square :    def __init__(self,s):        self.s = s    def squ(self):        return self.s * self.s    def cube(self):        return self.s * self.s * self.sprint("---------------welcome to calculator--------------------")print("# List of Choice :-")print("1) Add \n2) Subtract \n3) Multiply \n4) Division \n5) Power \n6) Square \n7) Cube\n8) EXIT")while(True):    try:        choice = int(input("# Please Enter The Choice :- "))        if (choice == 8):            break        if(choice>8 or choice<=0):            print('Sorry you Enter a invalid choice !!')            continue        if (choice!=6 and choice!=7):            x = None            y = None            while(True):                input_ = input("enter the 1st number = ")                if(input_.isnumeric()):  #isnumeric = the value is int the program is run otherwise no                    x = int(input_)                    break                else:                    print(f"sorry you put invalid input")                    continue            while (True):                input_ = input("enter the 2nd number = ")                if (input_.isnumeric()):                    y = int(input_)                    break                else:                    print(f"sorry you put invalid input")                    continue            c = Calculator(x,y)            if(choice==1):                print(f"Your Result is  = {c.add()}")            elif(choice==2):                print(f"Your Result is  = {c.sub()}")            elif(choice==3):                print(f"Your Result is  = {c.mul()}")            elif(choice==4):                print(f"Your Result is  = {c.div()}")            elif(choice==5):                print(f"Your Result is  = {c.pow()}")            else:                print("sorry you put invalid input")        elif(choice == 6):            s = int(input("enter the number = "))            S = Square(s)            print(f"Your Result is = {S.squ()}")        else:            s = int(input("enter the number = "))            S = Square(s)            print(f"Your Result is = {S.cube()}")        while(True):            want = input("do you want to continue(y/n) : ")            if (want == "n"):                exit()            elif(want == "y"):                break            else:                print("sorry you put invalid input")                continue    except ValueError as e :        print(f"sorry you put invalid input")