# PROJECT ONE GUESSES THE RANDOM VALUEimport randomrandnumber = random.randint(1,100)# print(randnumber)userguess = Noneguesses = 0while (userguess != randnumber):    userguess = int(input("enter your guess = "))    guesses += 1    if (userguess == randnumber):        print("you guessed it right!")    else:        if (userguess > randnumber):            print("you guessed it wrong! enter a smaller number")        else:            print("you guessed it wrong! enter a larger number")print(f"you guessed the number in {guesses} guesses")